class HomeFragment : BaseFragment<FragmentHomeBinding>(R.layout.fragment_home) {
    private val viewModel by viewModels<HomeViewModel>()
    override fun init() {
        val imageAdapter = DetailImageAdapter(
            listOf(R.drawable.banner1, R.drawable.banner2, R.drawable.banner3)
        ) {
            when (it) {
                R.drawable.banner1 -> {
                    startActivity(
                        Intent(
                            Intent.ACTION_VIEW,
                            Uri.parse("https://www.cyber1388.kr:447/")
                        )
                    )
                }
                R.drawable.banner2 -> {
                    startActivity(
                        Intent(
                            Intent.ACTION_VIEW,
                            Uri.parse("https://www.gov.kr/portal/yngbgsSportSvc.do?yngbgsSvcClsCd=01")
                        )
                    )
                }
                R.drawable.banner3 -> {
                    startActivity(
                        Intent(
                            Intent.ACTION_VIEW,
                            Uri.parse("https://www.gov.kr/portal/yngbgsFcltyYgList.do")
                        )
                    )
                }
            }
        }
        binding.viewpager.adapter = imageAdapter
        binding.wormDotsIndicator.attachTo(binding.viewpager)
        displayHtml()
        super.init()
    }

    private val noWantAdapter = HomePostAdapter(emptyList())

    override fun observe() {
        viewModel.getPostList()
        viewModel.postList.observe(viewLifecycleOwner) {
            it.map {
                when (it.indexa) {
                    1 -> {
                        
                    }
                    2 -> {
                        binding.tvTitle2.text = it.title
                        binding.tvContent2.text = it.content
                    }
                    3 -> {
                        binding.tvTitle3.text = it.title
                        binding.tvContent3.text = it.content
                    }
                }
            }
        }
        super.observe()
    }



